import os
import base64
preview_img_path = os.path.join(os.path.expanduser("~"), '.gui', 'preview.png')

def write_default_preview():
	img_data = ''
	data = base64.b64decode(img_data.encode())
	with open(preview_img_path, 'wb') as f:
		f.write(data)
		f.close()

if __name__ == "__main__":
	write_default_preview()
